const salesData = items[0].json;
const aggregatedData = {};

salesData.forEach(sale => {
    const region = sale.region;
    const category = sale.category;
    const amount = sale.amount;

    if (!aggregatedData[region]) {
        aggregatedData[region] = {};
    }
    if (!aggregatedData[region][category]) {
        aggregatedData[region][category] = 0;
    }
    aggregatedData[region][category] += amount;
});

return [{ json: aggregatedData }];

const aggregatedData = items[0].json;
const targets = { 'North': { 'Electronics': 1000, 'Clothing': 500 }, 'South': { 'Electronics': 800, 'Clothing': 600 } };
const results = [];

for (const region in aggregatedData) {
    for (const category in aggregatedData[region]) {
        const actual = aggregatedData[region][category];
        const target = targets[region] ? targets[region][category] : 0;
        results.push({ region, category, actual, target, met: actual >= target });
    }
}

return [{ json: results }];

const results = items[0].json;
let report = 'Sales Summary Report:\n';

results.forEach(result => {
    report += `Region: ${result.region}, Category: ${result.category}, Actual: ${result.actual}, Target: ${result.target}, Met: ${result.met ? 'Yes' : 'No'}\n`;
});

return [{ json: { report } }];
